// Home.js

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { auth, db } from "../firebaseConfig";
import { onAuthStateChanged } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";
import "../styles.css";
import backgroundImage from "../assets/background.png";

const Home = () => {
  const [user, setUser] = useState(null);
  const [isStaff, setIsStaff] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        try {
          const userDocRef = doc(db, "users", currentUser.uid); // Ensure the "users" collection exists
          const userDoc = await getDoc(userDocRef);

          if (userDoc.exists()) {
            const userData = userDoc.data();
            setUser(currentUser);
            setIsStaff(userData.role === "staff");
          } else {
            console.error("User document not found in Firestore.");
          }
        } catch (error) {
          console.error("Error fetching user data:", error);
        }
      } else {
        setUser(null);
        setIsStaff(false);
      }
      setLoading(false);
    });

    return () => unsubscribe(); // Cleanup listener on unmount
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Show a loading state
  }

  return (
    <div
      className="home-container"
      style={{
        height: "100vh",
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        position: "relative",
      }}
    >
      <div
        className="card"
        style={{
          background: "white",
          borderRadius: "15px",
          padding: "40px",
          width: "80%",
          maxWidth: "600px",
          textAlign: "center",
          boxShadow: "0 4px 30px rgba(0, 0, 0, 0.6)",
        }}
      >
        <h1>Welcome to the Birthday Party Reservation Portal!</h1>
        <p>Plan your next celebration with ease. Reserve, organize, and celebrate at Jollibee!</p>
        <br />
        <Link to="/reserve">
          <button className="action-btn">Make a Reservation</button>
        </Link>

        {user && isStaff && (
          <div style={{ marginTop: "20px" }}>
            <Link to="/dashboard">
              <button className="action-btn">Go to Dashboard</button>
            </Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
